import UIKit

protocol AuthenticationPresentationLogic {
    func presentLogin(response: AuthenticationScene.Login.Response)
    func presentError(error: AuthenticationScene.AuthenticationError)
}

class AuthenticationPresenter: AuthenticationPresentationLogic {
    weak var viewController: AuthenticationDisplayLogic?
    
    // MARK: Do something
    
    func presentLogin(response: AuthenticationScene.Login.Response) {
        let viewModel = AuthenticationScene.Login.ViewModel()
        DispatchQueue.main.async {
            self.viewController?.displayLoginSuccess(viewModel: viewModel)
        }
    }
    
    func presentError(error: AuthenticationScene.AuthenticationError) {
        let description: String
        switch error {
        case .noDevice(let msg):
            description = msg
        case .failedAuth(let msg):
            description = msg
        }
        let viewModel = ErrorViewModel(msg: description)
        DispatchQueue.main.async {
            self.viewController?.displayError(viewModel: viewModel)
        }
    }
}
